import React, { useState } from 'react';
import fakeData from '../../fakeData'
import Product from '../Product/Product';
import './Shop.css'

const Shop = () => {
    const first10 = fakeData.slice(0,10)
    console.log(fakeData);
   let [products, setProducts] =  useState(first10)
    console.log(first10);

    let [cart, setCart] = useState([])

    let handleCartButton= (product)=>{
        console.log('im clicked', product);
        // ... means previous initial cart value
        let newCart = [...cart, product]
        setCart(newCart)
    }

    return (
        <div className = 'shop-container'>
            <div className="product-container">
            <h1>This is shop component</h1>
            <h3>{products.length}</h3>

            {
            products.map(data => <Product handleCartButton = {handleCartButton} product = {data}></Product>)
            }

            </div>
            <div className="cart-container">
                <h1>Order Summary</h1>
                <p>Items ordered: {cart.length}</p>
            </div>

        </div>
    );
};

export default Shop;
