import React, { useState } from 'react';
import fakeData from '../../fakeData'
import { addToDatabaseCart } from '../../utilities/databaseManager';
import Cart from '../Cart/Cart';
import Product from '../Product/Product';
import './Shop.css'

const Shop = () => {
    const first10 = fakeData.slice(0,10)
    // console.log(fakeData);
   let [products, setProducts] =  useState(first10)
    // console.log(first10);

    let [cart, setCart] = useState([])

    let handleCartButton= (product)=>{
        // console.log('im clicked', product);
        // ... means previous initial cart 
        let newCart = [...cart, product]
        setCart(newCart)
        let sameProduct = newCart.filter(item => item.key === product.key)
        let count = sameProduct.length
        addToDatabaseCart(product.key, count)
    }

    return (
        <div className = 'shop-container'>
            <div className="product-container">
            <h1>This is shop component</h1>
            <h3>{products.length}</h3>

            {
            products.map(data => <Product showAddToCart = {true} handleCartButton = {handleCartButton} product = {data}></Product>)

            }

            </div>
            <div className="cart-container">
                <Cart cart = {cart}></Cart>
            </div>

        </div>
    );
};

export default Shop;
