import React from 'react';

const Cart = (props) => {
    console.log(props.cart, 'dekhi ki hoy');
    let cart = props.cart
    // cart.map(item => console.log(item.price, 'aije ami '))
    let total = 0
    for (let i = 0; i < cart.length; i++) {
        const element = cart[i];
        total= total + element.price      
    }

    let shippingCost = 0;

     if(total > 35){
        shippingCost = 0
    }
    else if(total >15){
        shippingCost = 4.99
    }
    else if(total > 0){
        shippingCost = 15.99
    }
    let initialTax = total * (10/100)
    let roundTax  = initialTax.toFixed(2)
    return (
        <div>
            <h1>Order Summary</h1>
            <p>Items ordered: {cart.length} </p>
            <p>Shipping cost: {shippingCost}</p>
            <p>Tax + Vat:{roundTax}</p>
            <p>Total price: {(total + shippingCost+ roundTax).toFixed(2)}</p>
        </div>
    );
};

export default Cart;
